mapper.select:
SELECT
-- 级联表的字段 ,jiazhangxuesheng.jiazhangxuesheng_jiazhangname as jiazhangxueshengJiazhangname ,jiazhangxuesheng.jiazhangxuesheng_phone as jiazhangxueshengPhone ,jiazhangxuesheng.jiazhangxuesheng_uuid_number as jiazhangxueshengUuidNumber ,jiazhangxuesheng.jiazhangxuesheng_xuesheng as jiazhangxueshengXuesheng ,jiazhangxuesheng.jiazhangxuesheng_id_number as jiazhangxueshengIdNumber ,jiazhangxuesheng.jiazhangxuesheng_photo as jiazhangxueshengPhoto ,jiazhangxuesheng.banji_types as banjiTypes ,jiazhangxuesheng.jiazhangxuesheng_nianji as jiazhangxueshengNianji ,jiazhangxuesheng.jiazhangxuesheng_email as jiazhangxueshengEmail ,jiazhangxuesheng.new_money as newMoney ,kaoshi.jiaoshi_id as kaoshiJiaoshiId ,kaoshi.kaoshi_uuid_number as kaoshiUuidNumber ,kaoshi.kaoshi_name as kaoshiName ,kaoshi.kaoshi_nian as kaoshiNian ,kaoshi.kaoshi_types as kaoshiTypes ,kaoshi.kaoshi_address as kaoshiAddress ,kaoshi.kaoshi_time as kaoshiTime ,kaoshi.kaoshi_content as kaoshiContent
FROM chengji a left JOIN jiazhangxuesheng jiazhangxuesheng ON a.jiazhangxuesheng_id = jiazhangxuesheng.id left JOIN kaoshi kaoshi ON a.kaoshi_id = kaoshi.id
and a.id in
#{item}
and ( a.kaoshi_id = #{params.kaoshiId} )
and ( a.jiazhangxuesheng_id = #{params.jiazhangxueshengId} )
and a.huida_defen >= #{params.huidaDefenStart}
and a.huida_defen <= #{params.huidaDefenEnd}
and a.paiming >= #{params.paimingStart}
and a.paiming <= #{params.paimingEnd}
and a.paiming = #{params.paiming}
and a.huida_content like CONCAT('%',#{params.huidaContent},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.jiazhangxuesheng_id IS NOT NULL
and jiazhangxuesheng.jiazhangxuesheng_jiazhangname like CONCAT('%',#{params.jiazhangxueshengJiazhangname},'%')
and jiazhangxuesheng.jiazhangxuesheng_phone like CONCAT('%',#{params.jiazhangxueshengPhone},'%')
and jiazhangxuesheng.jiazhangxuesheng_uuid_number like CONCAT('%',#{params.jiazhangxueshengUuidNumber},'%')
and jiazhangxuesheng.jiazhangxuesheng_xuesheng like CONCAT('%',#{params.jiazhangxueshengXuesheng},'%')
and jiazhangxuesheng.jiazhangxuesheng_id_number like CONCAT('%',#{params.jiazhangxueshengIdNumber},'%')
and jiazhangxuesheng.jiazhangxuesheng_nianji like CONCAT('%',#{params.jiazhangxueshengNianji},'%')
and jiazhangxuesheng.jiazhangxuesheng_email like CONCAT('%',#{params.jiazhangxueshengEmail},'%')
and jiazhangxuesheng.new_money >= #{params.newMoneyStart}
and jiazhangxuesheng.new_money <= #{params.newMoneyEnd}
and a.kaoshi_id IS NOT NULL
and kaoshi.jiaoshi_id = #{params.jiaoshiId}
and kaoshi.banji_types = #{params.banjiTypes}
and kaoshi.kaoshi_uuid_number like CONCAT('%',#{params.kaoshiUuidNumber},'%')
and kaoshi.kaoshi_name like CONCAT('%',#{params.kaoshiName},'%')
and kaoshi.kaoshi_nian like CONCAT('%',#{params.kaoshiNian},'%')
and kaoshi.kaoshi_types = #{params.kaoshiTypes}
and kaoshi.kaoshi_address like CONCAT('%',#{params.kaoshiAddress},'%')
and UNIX_TIMESTAMP(kaoshi.kaoshi_time) >= UNIX_TIMESTAMP(#{params.kaoshiTimeStart})
and UNIX_TIMESTAMP(kaoshi.kaoshi_time) <= UNIX_TIMESTAMP(#{params.kaoshiTimeEnd})
and kaoshi.kaoshi_content like CONCAT('%',#{params.kaoshiContent},'%')
and UNIX_TIMESTAMP(kaoshi.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(kaoshi.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
order by a.${params.orderBy}
